import React, {useState} from 'react';
import {AiOutlineReload} from 'react-icons/ai';
import axios from 'axios';
import $ from "jquery";
import { v4 as uuid } from 'uuid';
import handlePost from '../../../../services/fitnessai';


const baseURL = process.env.REACT_APP_URL

const GenerateMenu =() => {
    const [isLoading, setIsLoading] = useState(false);

    async function handlePost() {
      setIsLoading(true)
      axios.post(baseURL, {
        age: $("#age").val(),
        gender: $("#gender").val(),
        height: $("#height").val(),
        weight: $("#weight").val(),
        days_per_week: $("#days_per_week :selected").text(),
        experience: $("#experience :selected").text(),
        goal: $("#goal").val(),
        id: uuid()
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
      await new Promise(resolve => setTimeout(resolve, 2000));
      setIsLoading(false)
    }

    return (
      <div className = "generate">
        <button type="button" className="btn btn-success" onClick={handlePost} disabled = {isLoading} id="Generate">
            Generate <AiOutlineReload />
        </button>
      </div>
    )
}

export default GenerateMenu
