import { v4 as uuid } from 'uuid';
import React, {useState} from 'react';

const baseURL = process.env.REACT_APP_URL

export default async function handlePost() {
    const [isLoading, setIsLoading] = useState(false);
    setIsLoading(true)
    const id = uuid()
    axios.post(baseURL, {
      age: $("#age").val(),
      gender: $("#gender").val(),
      height: $("#height").val(),
      weight: $("#weight").val(),
      days_per_week: $("#days_per_week :selected").text(),
      experience: $("#experience :selected").text(),
      goal: $("#goal").val(),
      id: id
    })
    .then(function (response) {
      console.log(response);
      return fetchOutputs(id);  
    })
    .then(function(output) {
        console.log(output);
    })
    .catch(function (error) {
      console.log(error);
    });
    await new Promise(resolve => setTimeout(resolve, 2000));
    setIsLoading(false)
}


async function fetchOutputs (id) {
    try {
        const response = await axios.get(baseURL)
        let outputData;
        // Find the element with the same id
        for (let item of response.data.data) {
          let body = JSON.parse(item.body);
          if (body.plan.id === id) {
            outputData = body;
            break;
