import React, {useEffect, useState} from 'react'
import axios from 'axios';
import {AiOutlineReload} from 'react-icons/ai';
import $ from "jquery";

export function Output() {
  const [output, setOutput] = useState([])

  const fetchOutputs = async() => {
    const response = await fetch("http://localhost:8000/output")
    const outputs = await response.json()
    setOutput(outputs.data[outputs.data.length-1])
    }

  useEffect(() => {
    fetchOutputs()
  })

  useEffect(() => {
    setOutput([])
  }, [])


  return (
    <p> {output.body} </p>
  )
  }

export const GenerateMenu =() => {
    function Post() {
      axios.post('http://localhost:8000/menu', {
        liquor: $("#liquor :selected").text(),
        mixer: $("#mixer").val(),
        percentage: $("#percentage").val(),
        servings: $("#servings").val(),
        difficulty: $("#difficulty :selected").text(),
        holiday: $("#holiday :selected").text()
      })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
    }

    return (
        <button type="button" className="btn btn-success" onClick={Post}>
            Generate <AiOutlineReload />
        </button>
    )
}
